import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

import { Grid } from "@material-ui/core";
import { useDispatch } from "react-redux";
import { addBook } from "./store/action";
import img1 from "./img1.png";
import { BrushRounded } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
}));

export default function AddButton() {
  const classes = useStyles();
  const [book, setbook] = useState({
    img_src: img1,
    time_left: "60 min left",
    persons_read: "16k",
    book_name: "",
    author: "",
  });

  const [open, setopen] = useState(false);

  const aboutBook = (event) => {
    setbook({ ...book, [event.target.name]: event.target.value });
  };
  const dispatch = useDispatch();

  return (
    <div>
      <div
        className={classes.root}
        onClick={(event) => {
          setopen({ open: true });
        }}
      >
        <Button variant="contained" color="primary" href="#contained-buttons">
          ADD BOOK
        </Button>
      </div>
      <div>
        <Grid>
          <Grid container>
            <Button>x</Button>
          </Grid>
          <Grid conatiner>
            IMAGE_SOURCE : <input type="text"></input>
          </Grid>

          <Grid container>
            NAME OF THE BOOK:
            <input type="text" name="book_name" onChange={aboutBook}></input>
          </Grid>

          <Grid container>
            AUTHOR:
            <input type="text" name="author" onChange={aboutBook}></input>
          </Grid>
          <Grid container>
            <input
              type="submit"
              onClick={() => dispatch(addBook(book))}
            ></input>
          </Grid>
        </Grid>
      </div>
    </div>
  );
}

