import React, { useState } from "react";
import { Grid } from "@material-ui/core";
import { useDispatch } from "react-redux";
import { addBook } from "./store/action";
import img1 from "./img1.png";
import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';







function Add_Form(props) {
  const [book, setbook] = useState({
    img_src: img1,
    time_left: "60 min left",
    persons_read: "16k",
    book_name: "",
    author: "",
  });

  const styles={

  dialog:{
    width:"150vh"
  },

  button:{
    marginLeft:"2%",
    marginTop:"2%",
    marginBottom:"2%",
    marginRight:"2%"
  }

};

  const aboutBook = (event) => {
    setbook({ ...book, [event.target.name]: event.target.value });
  };
  const dispatch = useDispatch();
   const { onClose, selectedValue, open } = props;
  return (
    <Dialog style={styles.dialog} onClose={handleClose} aria-labelledby="simple-dialog-title" open={open}>
      <DialogTitle id="simple-dialog-title">BOOK DETAILS</DialogTitle>
      <DialogContent>
        <Grid>
      <Grid conatiner>
        IMAGE_SOURCE : <input type="text"></input>
      </Grid>
      <br></br>
      <Grid container>
        NAME OF THE BOOK:
        <input type="text" name="book_name" onChange={aboutBook}></input>
      </Grid>
      <br></br>

      <Grid container>
        AUTHOR:<input type="text" name="author" onChange={aboutBook}></input>
      </Grid>
      <br></br>

      <Grid container>
        <input type="submit" onClick={() => dispatch(addBook(book))}></input>
      <br></br>

      </Grid>
    </Grid>

      </DialogContent>

    </Dialog>

  );
}

export default Add_Form;
