import React from "react";
import Card from "@material-ui/core/Card";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import { AccessTime, AccountBox, MoreHoriz } from "@material-ui/icons";
import { Grid } from "@material-ui/core";
import { moveBook } from "./store/action";
import { useDispatch } from "react-redux";

function BookCard(props) {
  const styles = {
    image: {
      position: "relative",
      width: "100%",
      height: "100%",
    },
    // justify: {
    //   direction: "column",
    //   justify: "center",
    //   spacing: 5,
    // },
    cards: {
      width: "20%",
      padding: "0",
      height: "65%",
      marginRight: "0",
    },
    // div_image: {
    //   padding: 10,
    //   height: "60%",
    //   backgroundColor: "black",
    // },
    // horiz: {
    //   float: "right",
    // },
  };
  const dispatch = useDispatch();

  return (
    <Card style={styles.cards}>
      <div style={styles.div_image}>
        <img style={styles.image} alt=" " src={props.book.img_src}></img>
      </div>
      <Grid container style={styles.justify}>
        <Grid container style={{ paddingLeft: "6px" }}>
          <Typography>
            <b> {props.book.book_name}</b>
          </Typography>
        </Grid>
        <Grid container style={{ paddingLeft: "6px" }}>
          <Typography noWrap={true} component="p">
            {props.book.author}
          </Typography>
        </Grid>
        <Grid container>
          <Grid item>
            <IconButton>
              <AccessTime />
            </IconButton>
            {props.book.time_left}
          </Grid>
          <Grid item>
            <IconButton>
              <AccountBox />
            </IconButton>
            {props.book.persons_read}
          </Grid>
        </Grid>
        <Grid item style={{ padding: "4px" }}>
          <IconButton
            style={styles.horiz}
            onClick={() => {
              dispatch(moveBook(props.book));
            }}
          >
            <MoreHoriz />
          </IconButton>
        </Grid>
      </Grid>
    </Card>
  );
}

export default BookCard;
