import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button'
import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import Typography from '@material-ui/core/Typography';
import { blue } from '@material-ui/core/colors';
import { Grid } from "@material-ui/core";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addBook } from "./store/action";
import img1 from "./img1.png";




const useStyles = makeStyles({

  avatar: {
    backgroundColor: blue[100],
    color: blue[600],
  },
  button:{
    paddingLeft:"2%",
    paddingTop:"2%",
    paddingBottom:"2%",
    paddingRight:"2%"
  }
});

function SimpleDialog(props) {
  const [book, setbook] = useState({
    img_src: img1,
    time_left: "60 min left",
    persons_read: "16k",
    book_name: "",
    author: "",
  });
  const aboutBook = (event) => {
    setbook({ ...book, [event.target.name]: event.target.value });
  };

  const { onClose, selectedValue, open } = props;


  const handleClose = () => {
    onClose(selectedValue);
  };

  const dispatch = useDispatch();
  return (





    <Dialog onClose={handleClose} aria-labelledby="simple-dialog-title" open={open}>
      <DialogTitle id="simple-dialog-title">Set backup account</DialogTitle>
      <Grid>
      <Grid conatiner>
        IMAGE_SOURCE : <input type="text"></input>
      </Grid>

      <Grid container>
        NAME OF THE BOOK:
        <input type="text" name="book_name" onChange={aboutBook}></input>
      </Grid>

      <Grid container>
        AUTHOR:<input type="text" name="author" onChange={aboutBook}></input>
      </Grid>
      <Grid container>
        <input type="submit" onClick={() => dispatch(addBook(book))}></input>
      </Grid>
    </Grid>
    </Dialog>
  );
}

SimpleDialog.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired,
  selectedValue: PropTypes.string.isRequired,
};

export default function AddButton() {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = (value) => {
    setOpen(false);
  };
  const classes=useStyles

  return (
    <div>

      <br />
      <Button  color="primary" onClick={handleClickOpen} className={classes.button}>
        ADD BOOK
      </Button>
      <SimpleDialog  open={open} onClose={handleClose} />
    </div>
  );
}
