import { ADD_BOOK } from "./types";

const initalState = {
  finished: [
    {
      img_src: img1,
      book_name: "HARRY POTTER NEW SEASON",
      author: "VIVEK REDDY S",
      time_left: "15 min to read",
      persons_read: "15.6k read",
    },
    {
      img_src: img1,
      book_name: "HARRY POTTER NEW SEASON",
      author: "VIVEK REDDY S",
      time_left: "15 min to read",
      persons_read: "15.6k read",
    },
    {
      img_src: img1,
      book_name: "HARRY POTTER NEW SEASON",
      author: "VIVEK REDDY S",
      time_left: "15 min to read",
      persons_read: "15.6k read",
    },
  ],
  reading: [],
  currentView: null,
};
const booksReducer = (state = initalState, action) => {
  switch (action.type) {
    case ADD_BOOK:
      return { ...state, finished: [...state.finished, action.book] };
    default:
      return state;
  }
};

export default booksReducer;
