import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button'
import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';

import Typography from '@material-ui/core/Typography';
import { blue } from '@material-ui/core/colors';
import {Card} from "@material-ui/core";
import { Grid } from "@material-ui/core";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addBook } from "./store/action";
import img1 from "./img1.png";
import Add_Form from './Add_Form';



const styles={

  dialog:{
    width:"150vh"
  },
  button:{
    marginLeft:"2%",
    marginTop:"2%",
    marginBottom:"2%",
    marginRight:"2%"
  }
};

function SimpleDialog(props) {
  const [book, setbook] = useState({
    img_src: img1,
    time_left: "60 min left",
    persons_read: "16k",
    book_name: "",
    author: "",
  });
  const aboutBook = (event) => {
    setbook({ ...book, [event.target.name]: event.target.value });
  };

  const { onClose, selectedValue, open } = props;


  const handleClose = () => {
    onClose(selectedValue);
  };

  const dispatch = useDispatch();
  return (

    <Dialog style={styles.dialog} onClose={handleClose} aria-labelledby="simple-dialog-title" open={open}>
      <DialogTitle id="simple-dialog-title">BOOK DETAILS</DialogTitle>
      <DialogContent>
        <Add_Form/>

      </DialogContent>

    </Dialog>




  );
}

SimpleDialog.propTypes = {
  onClose: PropTypes.func.isRequired,
  open: PropTypes.bool.isRequired,

};

export default function AddButton() {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = (value) => {
    setOpen(false);
  };

  return (
    <div>

           <Button variant="outlined" color="primary" onClick={handleClickOpen} style={styles.button}>
        ADD BOOK
      </Button>
      <SimpleDialog  open={open} onClose={handleClose} />
    </div>
  );
}
