import { ADD_BOOK } from "./types";

const initalState = {
  finished: [],
  reading: [],
  currentView: null,
};
const booksReducer = (state = initalState, action) => {
  switch (action.type) {
    case ADD_BOOK:
      return { ...state, finished: [...state.finished, action.book] };
    default:
      return state;
  }
};

export default booksReducer;
