import React from "react";
import Card from "@material-ui/core/Card";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import { AccessTime, AccountBox, MoreHoriz } from "@material-ui/icons";
import { Link } from "react-router-dom";
import { Grid } from "@material-ui/core";

function BookCard(props) {
  const styles = {
    image: {
      width: "100%",
      height: "100%",
    },
    justify: {
      direction: "column",
      justify: "center",
      spacing: 5,
    },
    cards: {
      width: "20%",
      height: "65%",
    },
    div_image: {
      padding: 10,

      height: "60%",
      backgroundColor: "black",
    },
    horiz: {
      float: "right",
    },
  };
  return (
    <Card style={styles.cards}>
      <div style={styles.div_image}>
        <img style={styles.image} alt=" " src={props.book.img_src}></img>
      </div>
      <Grid container style={styles.justify}>
        <Grid conatainer style={{ paddingLeft: "6%" }}>
          <Typography>
            <b> {props.book.book_name}</b>
          </Typography>
        </Grid>
        <Grid container style={{ paddingLeft: "6%" }}>
          <Typography noWrap={true} component="p">
            {props.book.author}
          </Typography>
        </Grid>
        <Grid container>
          <IconButton>
            <AccessTime />
          </IconButton>
          {props.book.time_left}
          <IconButton>
            <AccountBox />
          </IconButton>
          {props.book.persons_read}
        </Grid>
        <Grid item style={{ padding: "4%" }}>
          <IconButton>
            <Link to="/book">
              <MoreHoriz />
            </Link>
          </IconButton>
        </Grid>
      </Grid>
    </Card>
  );
}

export default BookCard;
