import React from "react";
import Card from "@material-ui/core/Card";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import { AccessTime, AccountBox, MoreHoriz } from "@material-ui/icons";
import { Grid, CardActions, Button, CardMedia } from "@material-ui/core";
import { moveBook } from "./store/action";
import { useDispatch } from "react-redux";
import { useStyles } from "react";

const BookCard = (props) => {
  const dispatch = useDispatch();
  return (
    // <Grid container alignItems="center">
    // <Grid item>
    <Card className="root" style={{ paddingBottom: "20px" }}>
      <Grid container direction="column">
        <Grid item xs>
          <CardMedia
            style={{ height: "284px" }}
            image="img1.png"
            title="Contemplative Reptile"
          />
        </Grid>

        <Grid item>
          <p className="title">{props.book.title}</p>
        </Grid>
        <Grid item>
          <p className="author">{props.book.author}</p>
        </Grid>
        <Grid item>
          <IconButton>
            <AccessTime />
          </IconButton>
          <span className="read">{props.book.timeToRead}</span>
        </Grid>
        <Grid item>
          <CardActions>
            {!props.disable && (
              <Button
                variant="contained"
                color="primary"
                onClick={() => dispatch(moveBook(props.book))}
              >
                Finish
              </Button>
            )}
          </CardActions>
        </Grid>
      </Grid>
    </Card>
    // </Grid>
    // </Grid>
  );
};

export default BookCard;
