import { ADD_BOOK, REMOVE_BOOK } from "./types";

export const addBook = (book) => {
  return {
    type: ADD_BOOK,
    book: book,
  };
};

export const removeBook=(book)=>{
  return {
    type:REMOVE_BOOK,
    book:book,
  };
};
